DO $$
DECLARE
    emp RECORD;
    v_total_cost DECIMAL(10,2) := 0;
    v_budget DECIMAL(10,2) := 15000.00;
    v_employee_count INT := 0;
BEGIN
    -- Loop through all Engineering employees (BREAKPOINT)
    FOR emp IN 
        SELECT id, name, salary 
        FROM employees 
        WHERE department = 'Engineering'
        ORDER BY salary ASC
    LOOP
        v_employee_count := v_employee_count + 1;  -- BREAKPOINT
        
        -- Calculate 10% raise
        DECLARE
            v_raise_amount DECIMAL(10,2);
            v_result RECORD;
        BEGIN
            v_raise_amount := emp.salary * 0.10;  -- BREAKPOINT
            v_total_cost := v_total_cost + v_raise_amount;
            
            -- Check budget (BREAKPOINT)
            IF v_total_cost > v_budget THEN
                RAISE NOTICE 'Budget exceeded after % employees', v_employee_count;
                RAISE EXCEPTION 'Budget limit reached';
            END IF;
            
            -- Apply raise (BREAKPOINT)
            SELECT * INTO v_result 
            FROM give_raise(emp.id, v_raise_amount, '10% Engineering raise');
            
            RAISE NOTICE 'Employee: % - Raise: $%', emp.name, v_raise_amount;
        END;
    END LOOP;
    
    RAISE NOTICE 'Total raises given: %, Total cost: $%', v_employee_count, v_total_cost;
END $$

