CREATE OR REPLACE FUNCTION add_discount(product_id INT, discount DECIMAL(10,2))
RETURNS TEXT AS $$
DECLARE
    old_price DECIMAL(10,2);
BEGIN
    SELECT price INTO old_price FROM products WHERE id = product_id;
    UPDATE products SET price = price - discount WHERE id = product_id;
    RETURN 'Discount applied!';
END;
$$ LANGUAGE plpgsql

-- Create a simple calculator procedure
CREATE OR REPLACE FUNCTION calculate_price(
    base_price DECIMAL(10,2),
    quantity INT,
    discount_percent DECIMAL(5,2)
)
RETURNS TABLE(
    step VARCHAR(50),
    value DECIMAL(10,2)
) AS $$
DECLARE
    subtotal DECIMAL(10,2);
    discount_amount DECIMAL(10,2);
    final_price DECIMAL(10,2);
BEGIN
    -- Step 1: Calculate subtotal
    subtotal := base_price * quantity;
    RETURN QUERY SELECT 'Subtotal'::VARCHAR, subtotal;
    
    -- Step 2: Calculate discount
    discount_amount := subtotal * (discount_percent / 100);
    RETURN QUERY SELECT 'Discount'::VARCHAR, discount_amount;
    
    -- Step 3: Calculate final price
    final_price := subtotal - discount_amount;
    RETURN QUERY SELECT 'Final Price'::VARCHAR, final_price;
END;
$$ LANGUAGE plpgsql

