CREATE OR REPLACE PROCEDURE process_train_departure(
    p_train_id INT,
    p_scheduled_time TIMESTAMP,
    p_actual_time TIMESTAMP
)
LANGUAGE plpgsql
AS $$
DECLARE
    v_delay_minutes INT;
    v_status TEXT;
BEGIN
    -- 1. Calculate delay in minutes
    v_delay_minutes := EXTRACT(EPOCH FROM (p_actual_time - p_scheduled_time)) / 60;

    -- 2. Determine status based on delay
    IF v_delay_minutes <= 0 THEN
        v_status := 'On Time';
        v_delay_minutes := 0;
    ELSIF v_delay_minutes < 30 THEN
        v_status := 'Slight Delay';
    ELSE
        v_status := 'Heavy Delay';
    END IF;

    -- 3. Update the train tracking table
    UPDATE train_tracking
    SET departure_status = v_status,
        delay_amount = v_delay_minutes,
        last_updated = NOW()
    WHERE train_id = p_train_id;

    -- Log the result (visible in debug results)
    RAISE NOTICE 'Train % processed with status: %', p_train_id, v_status;
END;
$$

